s=%i*w;
g=1../(s.^2+s+1);
clf("reset")

semilogx(w,abs(g));

title("$\LARGE \left|1/(s^2+s+1)\right|\mbox{ for }s=i\omega$")
xlabel("$\LARGE \omega$")
