// Très grand déterminant :
n = 1000;
A = rand(n, n);
det(A)
[e, m] = det(A)

// Très petit déterminant (d'une matrice encodée creuse) :
A = (triu(sprand(n,n,1)) + diag(rand(1,n)))/1.5;
det(A)
prod(diag(A))
[e, m] = det(A)
A = A/2;
det(A)
[e, m] = det(A)

s = %s;
det([s, 1+s ; 2-s, s^2])

